export const initialState={
    basket:[
        {
        id:"12321341",
        title :"The Power of your subconscious Mind",
        price:11.96,
        rating:4,
        image:"https://images-na.ssl-images-amazon.com/images/I/51QTTApN-XL._SX324_BO1,204,203,200_.jpg"
    },
    {
        id:"12321341",
